package Logic;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int xx=3,yy=4;

		int or= xx|yy;
		int and=xx&yy;
		int xor=xx^yy;

		System.out.println("or="+or);
		System.out.println("and="+and);
		System.out.println("xor="+xor);
	}

}
