package LoopStatement;

public class Forloop_pat2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=1;

		for (int i=5;i<=num;i++) {
			
			for(int j=1;j<=i;j++) {
			
				System.out.print(i+" ");
					

			}
			System.out.println();
		}


				
				
				
	}

}
